/* calculates number of rows and also physical size - with the help of a temporary table */
CREATE TABLE #sizes_tables (
    table_name NVARCHAR(128),
    rows CHAR(11),
    reserved VARCHAR(18),
    data VARCHAR(18),
    index_size VARCHAR(18),
    unused VARCHAR(18)
);

EXEC sp_MSForEachTable 'INSERT INTO #sizes_tables EXEC sp_spaceused ''?'' ';
 
SELECT ist.table_name, convert(bigint,rows) AS nr_of_rows,
        CONVERT(decimal, LEFT(data, LEN(data)-3))/1024 AS size_MB
  FROM #sizes_tables, information_schema.tables ist
 WHERE table_catalog= 'nucleus_staging_sg'
   AND table_schema  = 'dbo'
